widgets.onValue:
setVar("button_" + get("current_track") , "lastVariation" , value )
set("button_track_device_params",1)
//send("/send/variation/" + getVar(this,"device"),value)
// let params = getVar("params_" + get("current_track"),"params")
// params.forEach ( p => {
//   send("/send/refresh/param/" + parseInt(p[3]),1)
// })

let current = get("current_variation")
if ( value && current > 1 ){
  let newValue = current - 1
  set("current_variation",newValue)
  set("button_send_variation",1)
}

let current = get("current_variation")
let total = get("device_variations")
if ( value && current < total ){
  let newValue = current + 1
  set("current_variation",newValue)
  set("button_send_variation",1)
}

var devices = getVar("devices_" + get("current_track"),"devices")
let device = devices[0]
if ( value ){
  send("/send/store/variation/"+getVar(this,"device"),1)
  let variations = parseInt(device[2])
  set("device_variations",variations+1)
  device[2] = variations+1
  devices[0] = device
  setVar("devices_" + get("current_track"),"devices",devices)
}

