let track = get("current_track")
let deviceIndex = get("current_device_index")
let device = "devices_" + track
let devices = getVar(device,"devices")
setVar("button_next_variation","device",devices[deviceIndex][4])
setVar("button_prev_variation","device",devices[deviceIndex][4])
setVar("button_send_variation","device",devices[deviceIndex][4])

let index = getProp(this,"id").replace('button_','')
let target = "params_" + index
let params = getVar(target,"params")
let visible_macros = getVar("devices_" + index,"devices")[0][4]
let n = 0;
params.forEach( (p,i) => {
  if ( !p[4].includes("Device On") ){
    if ( n <= visible_macros ){
      n++;
      set("param_lbl_" + n,p[4].replace(' ','\n').replace('"','').replace('"',''))
      set("fader_param_" + n,parseFloat(p[5]))
      setVar("fader_param_" + n,"range", {min:parseFloat(p[6]),max:parseFloat(p[7])})
      setVar("fader_param_" + n,"device",parseInt(p[3]))
    }
  }
})

//set current track indicator
set("current_track",index)


let device = "devices_" + index
let devices = getVar(device,"devices")

//set the current device the first of the track
set("current_device",devices[0][1].replace('"','').replace('"',''))
setVar("current_device","color",getVar(this,"color"))


setVar("current_track","color",getVar(this,"color"))
//set variation_count to device_variations 
set("device_variations",parseInt(devices[0][2]))
//set device id to current_variation
setVar("current_variation","device",parseInt(devices[0][4]))
//get the last variation selected for the current device
set("current_variation",getVar(this,"lastVariation"))

//Assign device id to randomize button
setVar("button_randomize","device",parseInt(devices[0][4]))

let xypad = getVar(this,"xypad")
if ( xypad.xDevice.id && xypad.yDevice.id ){
  setVar("xy_1","xDevice",xypad.xDevice.id)
  setVar("xy_1","yDevice",xypad.yDevice.id)
  set("lbl_x_param_name",xypad.xDevice.name)
  set("lbl_y_param_name",xypad.yDevice.name)
} else {
  setVar("xy_1","xDevice",0)
  setVar("xy_1","yDevice",0)
  set("lbl_x_param_name","")
  set("lbl_y_param_name","")
}
setVar("button_track_devices","devices",devices)

let devs = { "labels" : [],"values" : [] }
if ( devices.length > 1 ){
  devices.map ( (d,index) => {
    devs.labels.push (d[1].replace('"','').replace('"',''))
    devs.values.push (index)
  })
  setVar("menu_devices","visible",true)
} else {
  setVar("menu_devices","visible",false)
}
// setVar("dropdown_devices","options",devs)
setVar("menu_devices","options",devs)
setVar("menu_devices","color",getVar(this,"color"))
